mapper.select:
SELECT tmp.mark_Id AS code, tmp.mark_name AS name, sml.pre_close_price AS preClosePrice, sml.open_price AS openPrice, tmp.current_price AS tradePrice, tmp.updown_rate AS upDown, tmp.trade_account AS tradeAmt, tmp.trade_volume AS tradeVol, DATE_FORMAT( tmp.cur_time, '%Y%m%d') AS curDate FROM ( SELECT * FROM stock_market_index_info AS smi WHERE smi.cur_time =#{lastDate} AND smi.mark_Id IN
#{id}
) AS tmp LEFT JOIN stock_market_log_price AS sml ON tmp.mark_Id=sml.market_code AND DATE_FORMAT( sml.cur_date, '%Y%m%d' )= DATE_FORMAT(#{lastDate},'%Y%m%d' )

SELECT tmp.mark_name AS name, tmp.cur_point AS curPoint, tmp.current_price AS upDownPoint, tmp.updown_rate AS upDownRate, DATE_FORMAT( tmp.cur_time, '%Y%m%d') AS curTime FROM ( SELECT * FROM stock_market_index_info AS smi WHERE smi.cur_time =#{lastDate} AND smi.mark_Id IN
#{id}
) AS tmp LEFT JOIN stock_market_log_price AS sml ON tmp.mark_Id=sml.market_code AND DATE_FORMAT( sml.cur_date, '%Y%m%d' )= DATE_FORMAT(#{lastDate},'%Y%m%d' )

select date_format(smi.cur_time,'%Y%m%d%H%i') as time, sum(smi.trade_account) as count from stock_market_index_info as smi where smi.cur_time between #{openDate} and #{stockDateTime} and smi.mark_Id in
#{id}
group by time
